module PartA where

import Control.Monad.State

parseInput :: String -> String
parseInput = id

solveA :: String -> String
solveA = id

readFloor [] = 0
readFloor ('(':cs) = readFloor cs + 1
readFloor (')':cs) = readFloor cs - 1
readFloor _ = undefined 

type Count = Int 
type Value = Int

step :: String -> (Value, String)
step ('(':cs) = ( 1, cs)
step (')':cs) = (-1, cs)
step _ = undefined 

findBasementFrom :: Count -> Value -> State String Count
findBasementFrom count soFar = do
  nextValue <- state step
  let x = nextValue + soFar
  if x == -1
  then return count
  else 
    findBasementFrom (count + 1) x

findBasement :: String -> Int 
findBasement = evalState (findBasementFrom 1 0)

b = findBasement ")"
c = findBasement "()())"
a = findBasement input

---

translate :: Num p => Char -> p
translate '(' =  1
translate ')' = -1
translate _   =  undefined

inputNum :: [Int]
inputNum = map translate input

basementPos :: Count -> Value -> [Int] -> Maybe Int
basementPos count val [] = Nothing
basementPos count val (i:is) = if val == -1 then Just count else basementPos (count + 1) (val + i) is

d = basementPos 0 0 inputNum
